'use strict';

var getCommunitySubscriptionTemplate = function() {
    return function(id) {
        return '\n<div id="'.concat(id, '" data-test-id="communitySubscription">\n  <style>\n    #').concat(id, " iframe {\n      position: absolute;\n      top: 0;\n      left: 0;\n      opacity: 0;\n      pointer-events: none;\n      border: none;\n      color-scheme: auto;\n    }\n      #").concat(id, '[data-state=loaded] iframe {\n      position: fixed;\n      opacity: 100;\n      pointer-events: all;\n      z-index: 99999;\n    }\n  </style>\n  <iframe width="100%" height="100%" />\n</div>');
    };
};

exports.getCommunitySubscriptionTemplate = getCommunitySubscriptionTemplate;
